mapper.select:
select *
from tour_inquiry
where areacode = #{AREACODE}
and cat3 = #{STAY_CODE}
and rownum < 21
order by readcount desc

mapper.update:
update preference
set areacode_ref = #{areacode},
	age = #{age},
    grouptrip = #{grouptrip},
    purpose_code = #{purpose_code},
    stay_code = #{stay_code}
where seq_preference = #{seq_preference}
  and m_id = #{m_id}
